mapper.update:
UPDATE sj_retry_summary rt, (
SELECT #{item.runningNum} AS running_num, #{item.finishNum} AS finish_num, #{item.maxCountNum} AS max_count_num, #{item.suspendNum} AS suspend_num, #{item.triggerAt} AS trigger_at, #{item.sceneName} AS scene_name, #{item.namespaceId} AS namespace_id, #{item.groupName} AS group_name
) tt SET rt.running_num = tt.running_num, rt.finish_num = tt.finish_num, rt.max_count_num = tt.max_count_num, rt.suspend_num = tt.suspend_num WHERE rt.trigger_at = tt.trigger_at AND rt.group_name = tt.group_name AND rt.namespace_id = tt.namespace_id AND rt.scene_name = tt.scene_name
